import styled from 'styled-components';
import { Link } from 'react-router-dom';

export const Button = styled(Link) `
    width: 100%auto;

    align-items: center;
    justify-content: center;

    color : #ffffff;

    font-size:  1.5vw;
    font-style: normal;
    font-weight: 600;

    line-height: 2.5vw;
    letter-spacing: 0em;

    text-align: center;
    text-decoration : none;

    background-color: #365A6B;
    border-radius: 1vw;

`
