    justify-content: center;

    margin-top: 4vh;

    span{
        font-size: 4vw;
        a{
            font-size: 4vw;
        }
    }
`;

export const LoginBox = styled.div`
    width: 90%;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;

    div{
        display: flex;
        flex-direction: column;
        align-items: flex-start;
        justify-content: center;
        width: 100%;
        height: 3vh;

        label{
            color: #4C7183;
            font-size: 4vw;
            font-weight: 500;
            /* margin-bottom: -20px; */
            bottom: 0;
        }
    }
`;
