import styled from 'styled-components';

export const Text = styled.h1 `
    color :   #4C7183;
    font-size :  3.8vw;
    font-family:'Roboto';
    font-style: normal;
    font-weight: 300;
    line-height: 5vw;
    letter-spacing: 0em;
    text-align: center;

    @media(min-width: 768px){
        font-size :  2vw;
    }

`
