import styled from 'styled-components';

export const Main = styled.div`
    width: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;

    padding-top: 10%;

    span{
        font-size: 4vw;
        a{
            font-size: 4vw;
            color: #7FEA6E;
        }
    }

    div{
        margin-top: 3vh;
    }

    .Main{
        @media(min-width: 768px){
            width: 65%;
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
        }
    }
`;

export const LoginBox = styled.div`
    width: 90%;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;

    div{
        display: flex;
        flex-direction: column;
        align-items: flex-start;
        justify-content: center;
        width: 100%;
        height: 3vh;

        label{
            color: #4C7183;
            font-size: 4vw;
            font-weight: 500;
            /* margin-bottom: -20px; */
            bottom: 0;
        }
    }
`;

export const ButtonArea = styled.div`
    width: 90%;
`;
