import styled from "styled-components";

export const Label = styled.label`
    color: #4C7183;
    font-size: 4vw;
    font-weight: 500;
    margin-bottom: -20px;
    bottom: 0;
    @media(min-width: 768px){
        font-size :  3vw;
    }
    @media(min-width: 1080px){
        font-size :  1.5vw;
    }
`
