import styled from 'styled-components';

export const Title = styled.h1 `
    color :   #000;
    font-size :  3vw;
    font-family:'Roboto';
    font-style: normal;
    font-weight:  ${props=>props.titleBold ? '700': '300' };
    line-height: 5vw;
    letter-spacing: 0em;
    text-align: center;
    padding-bottom: 3%;
    padding-left :5%;
    padding-right:5%;

`
